INSERT INTO `wp_posts` VALUES("3679", "1", "2017-06-07 16:10:20", "2017-06-07 08:10:20", "<p class=\"MsoNormal\" style=\"margin-left: 0.5in;text-align: justify\"><span>As a\nProject Engineer, the broad area of your job responsibilities will cover the\nfollowing:&nbsp;</span></p>\n\n<p class=\"MsoListParagraph\" style=\"margin-left: 1.0in;text-align: justify;text-indent: -0.25in\"><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n</span></span><span>Assist Project Manager in day to day site operation</span></p>\n\n<p class=\"MsoListParagraph\" style=\"margin-left: 1.0in;text-align: justify;text-indent: -0.25in\"><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n</span></span><span>Attend site meetings and inspections</span></p>\n\n<p class=\"MsoListParagraph\" style=\"margin-left: 1.0in;text-align: justify;text-indent: -0.25in\"><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n</span></span><span>Work closely with internal team and external parties for project\nplanning, execution, monitoring and closing of projects</span></p>\n\n<p class=\"MsoListParagraph\" style=\"margin-left: 1.0in;text-align: justify;text-indent: -0.25in\"><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n</span></span><span>Preparation of shop drawings and as built documents</span></p>\n\n<p class=\"MsoListParagraph\" style=\"margin-left: 1.0in;text-align: justify;text-indent: -0.25in\"><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n</span></span><span>Plan and execute project at micro level</span></p>", "Project Engineer", "", "publish", "closed", "closed", "", "project-engineer", "", "", "2017-06-22 09:34:36", "2017-06-22 01:34:36", "", "0", "http://localhost/enggsol/jobs/project-engineer/", "0", "jobs", "", "0");
