INSERT INTO `wp_posts` VALUES("3687", "1", "2017-06-07 16:10:21", "2017-06-07 08:10:21", "<p class=\"MsoNormal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Typically 2-4 years IT experience</span></p><p class=\"MsoNormal\"><span style=\"font-family: Arial , sans-serif;font-size: 10.0pt;\">Have\n     implementation experience in IBM Maximo</span></p>\n\n<p class=\"MsoNormal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Familiar with Software Development Life Cycle (SDLC)</span></p>\n\n<p class=\"MsoNormal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Ability to produce specifications for documentations and implementations</span></p>\n\n<p class=\"MsoNormal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Experience in IT business analysis techniques</span></p>\n\n<p class=\"MsoNormal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">&nbsp;Degree Holder</span></p>\n\n<p class=\"MsoNormal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Self-motivated with a positive can-do attitude, creative with excellent\npresentation, communication, negotiation and interpersonal skills including\nstrong persuasive techniques.</span></p>\n\n<p class=\"MsoNormal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Ability to lead, develop and maintain respectful and trusting relationship;</span></p>\n\n<p class=\"MsoNormal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Proficient in written and spoken English</span></p>\n\n<span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Able to travel as required</span>", "IBM Maximo Consultant/Sr Consultant", "", "publish", "closed", "closed", "", "ibm-maximo-consultantsr-consultant", "", "", "2017-06-07 18:50:33", "2017-06-07 10:50:33", "", "0", "http://localhost/enggsol/jobs/ibm-maximo-consultantsr-consultant/", "0", "jobs", "", "0");
INSERT INTO `wp_posts` VALUES("3688", "1", "2017-06-07 16:10:21", "2017-06-07 08:10:21", "<table class=\"MsoNormalTable\" border=\"0\" cellspacing=\"0\" cellpadding=\"0\" width=\"737\" style=\"width: 552.85pt;margin-left: 13.95pt;border-collapse: collapse;\"><tbody><tr><td width=\"577\" valign=\"top\" style=\"width: 432.4pt;border-top: none;border-left: none;border-bottom: solid windowtext 1.0pt;border-right: solid windowtext 1.0pt;padding: 0.0in 5.4pt 0.0in 5.4pt;\"><p class=\"MsoNormal\"></p><ul><li>At least 5 years experience with substantial project work in software design\n  &amp; development</li><li>Strong technical experience in system / applications architecture design</li><li>Experience in designing solutions using appropriate platforms and system\n  technologies such as enterprise database design, web and application server\n  and network technologies</li><li>Have\n       implementation experience in IBM Maximo and have implemented at least 1\n       system (full SDLC) using IBM Maximo.</li></ul><div>Requirements:</div><ul><li>Degree in Computer Science and Engineering</li><li>Certified in appropriate technologies is an advantage</li><li>Self-motivated with a positive can-do attitude, creative with excellent\n  presentation, communication, negotiation and interpersonal skills including\n  strong persuasive techniques.</li><li>Ability to lead, develop and maintain respectful and trusting relationship;</li><li>Proficient in written and spoken English</li><li>Able to travel as required</li></ul><p></p>\n  \n  \n  \n  \n  \n  \n  \n  \n  </td>\n </tr>\n</tbody></table>", "Architect / Associate Architect (IBM Maximo)", "", "publish", "closed", "closed", "", "architect-associate-architect-ibm-maximo", "", "", "2017-06-07 18:50:33", "2017-06-07 10:50:33", "", "0", "http://localhost/enggsol/jobs/architect-associate-architect-ibm-maximo/", "0", "jobs", "", "0");
INSERT INTO `wp_posts` VALUES("3689", "1", "2017-06-07 16:10:21", "2017-06-07 08:10:21", "<p class=\"MsoNormal\"></p><ul><li>Typically\n9 years IT project management experience</li><li>Have\n     implementation experience in IBM Maximo and have implemented at least 1\n     system (full SDLC) using IBM Maximo.</li><li>Experience in the development, implementation and support of information\ntechnology projects in a range of&nbsp;<font face=\"Arial, sans-serif\" style=\"font-size: medium;\">organisations</font></li><li>Degree in Information Systems or equivalent</li><li>Self-motivated with a positive can-do attitude, creative with excellent\npresentation, communication, negotiation and interpersonal skills including\nstrong persuasive techniques.</li><li>Able to lead, develop and maintain respectful and trusting relationship</li><li>Proficient in written and spoken English</li><li>Able to travel as required</li></ul><p></p>", "PM / Sr PM(IBM Maximo)", "", "publish", "closed", "closed", "", "pm-sr-pmibm-maximo", "", "", "2017-06-07 18:50:33", "2017-06-07 10:50:33", "", "0", "http://localhost/enggsol/jobs/pm-sr-pmibm-maximo/", "0", "jobs", "", "0");
INSERT INTO `wp_posts` VALUES("3690", "1", "2017-06-07 16:10:21", "2017-06-07 08:10:21", "<div><b>Job Title:</b></div>\r\n<div><b></b></div>\r\n<div><b>Education / Experience:</b></div>\r\n<div>Diploma in Computers / Engineering or equivalent</div>\r\n<div>ITE holders with working experiences can be considered</div>\r\n<div>At least 1 year of relevant working experience preferred</div>\r\n<div></div>\r\n<div><b>Job Description:</b></div>\r\n<div>Assist in installation of systems during project implementation stage (Install, configure and administer network, OS, Storage and backup)</div>\r\n<div>Co-ordinate periodic system upgrade (OS or application)</div>\r\n<div>Provide post-sale incident and problem management support, co-ordinate with external parties and customers for problem resolutions</div>\r\n<div>Stand-by support is required</div>\r\n<div></div>\r\n<div><b>Skill sets:</b></div>\r\n<div>Basic knowledge of IT Infrastructure such as OS, network, storage and backup</div>\r\n<div>With one or more of the following skillsets will be an added advantage:</div>\r\n<div>Knowledge and skills in storage network</div>\r\n<div>OS skills in Enterprise UNIX</div>\r\n<div>Technology background and skills in Networking (CCNA/CCNP); Virtualization (VCP); backup and recover</div>\r\n<div>Keen to pick up new technology and skillsets</div>\r\n<div>Good team worker who can carry out assignments independently</div>\r\n<div></div>", "Associate Engineer / Engineer / Senior Engineer (SLA)", "", "publish", "closed", "closed", "", "associate-engineer-engineer-senior-engineer-sla", "", "", "2017-06-07 18:25:50", "2017-06-07 10:25:50", "", "0", "http://localhost/enggsol/jobs/associate-engineer-engineer-senior-engineer-sla/", "0", "jobs", "", "0");
