INSERT INTO `wp_posts` VALUES("3692", "1", "2017-06-07 18:20:12", "2017-06-07 10:20:12", "<p class=\"MsoNormal\" style=\"text-align: justify;line-height: 12.0pt;background: white\"><b><span>Job Responsibilities:</span></b><span></span></p>\n\n<ul>\n <li class=\"MsoNormal\"><span style=\"font-size: 7.5pt;font-family: Arial , sans-serif\">Responsible\n     to set-up and operate any of the following machines</span></li>\n <ul>\n  <li class=\"MsoNormal\"><span style=\"font-size: 7.5pt;font-family: Arial , sans-serif\">Deep\n      Hole Drilling Machine</span></li>\n  <li class=\"MsoNormal\"><span style=\"font-size: 7.5pt;font-family: Arial , sans-serif\">Honing\n      Machine</span></li>\n  <li class=\"MsoNormal\"><span style=\"font-size: 7.5pt;font-family: Arial , sans-serif\">Manual\n      Lathe</span></li>\n  <li class=\"MsoNormal\"><span style=\"font-size: 7.5pt;font-family: Arial , sans-serif\">CNC\n      Lathe</span></li>\n  <li class=\"MsoNormal\"><span style=\"font-size: 7.5pt;font-family: Arial , sans-serif\">CNC\n      7-axis Turn Mill</span></li>\n  <li class=\"MsoNormal\"><span style=\"font-size: 7.5pt;font-family: Arial , sans-serif\">CNC\n      VTL</span></li>\n </ul>\n <li class=\"MsoNormal\"><span style=\"font-size: 7.5pt;font-family: Arial , sans-serif\">Able\n     to perform simple programming (For CNC machines Only).</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 7.5pt;font-family: Arial , sans-serif\">Conduct\n     first piece inspection.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 7.5pt;font-family: Arial , sans-serif\">Report\n     to management in the event of non-conformance of components.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 7.5pt;font-family: Arial , sans-serif\">Adhere\n     to good quality, housekeeping and safety practices.</span></li>\n</ul>\n\n<p class=\"MsoNormal\" style=\"text-align: justify;line-height: 12.0pt;background: white\"><span>&nbsp;</span></p>\n\n<p class=\"MsoNormal\" style=\"text-align: justify;line-height: 12.0pt;background: white\"><b><span>Requirements:</span></b><span></span></p>\n\n<ul>\n <li class=\"MsoNormal\"><span style=\"font-size: 7.5pt;font-family: Arial , sans-serif\">NTC\n     2 or 3 in Precision Engineering or equivalent.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 7.5pt;font-family: Arial , sans-serif\">Experience\n     in the machine applied for will be an added advantage</span></li><li class=\"MsoNormal\">Able\n     to read &amp; understand engineering drawings will be an added advantage.</li>\n <li class=\"MsoNormal\"><span style=\"font-size: 7.5pt;font-family: Arial , sans-serif\">Capable\n     of using measuring instruments.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 7.5pt;font-family: Arial , sans-serif\">Able\n     to work shift (2 weeks rotating: morning shift 7.30am  4.45pm &amp; night\n     shift 7.30pm  4.45am) and overtime.</span></li>\n</ul>", "Machinist", "", "publish", "closed", "closed", "", "machinist", "", "", "2017-07-06 09:30:30", "2017-07-06 01:30:30", "", "0", "http://localhost/enggsol/jobs/machinist/", "0", "jobs", "", "0");
INSERT INTO `wp_posts` VALUES("3693", "1", "2017-06-13 14:09:26", "2017-06-13 06:09:26", "<table class=\"MsoNormalTable\" border=\"1\" cellspacing=\"0\" cellpadding=\"0\" width=\"657\" style=\"border-collapse: collapse;border: none\">\n <tbody><tr>\n  <td width=\"168\" valign=\"top\" style=\"width: 125.9pt;border: solid windowtext 1.0pt;padding: 0.0in 5.4pt 0.0in 5.4pt\">\n  <p class=\"MsoNormal\" style=\"margin-bottom: 0.0in\">Job Scope:</p>\n  </td>\n  <td width=\"489\" valign=\"top\" style=\"width: 367.1pt;border: solid windowtext 1.0pt;border-left: none;padding: 0.0in 5.4pt 0.0in 5.4pt\">\n  <p class=\"MsoListParagraph\" style=\"margin-left: 0.0in\"><span><b>Job Scope for Procurement\n  Engineer / Executive:</b></span></p>\n  <p class=\"MsoListParagraph\" style=\"text-indent: -0.25in\"><span>-<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n  </span></span><span>To conduct sourcing exercise for potential suppliers.</span></p>\n  <p class=\"MsoListParagraph\" style=\"text-indent: -0.25in\"><span>-<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n  </span></span><span>To conduct all Tender Processes and Processing of Purchase Order in\n  accordance to Corporate Governance Regulations.</span></p>\n  <p class=\"MsoListParagraph\" style=\"text-indent: -0.25in\"><span>-<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n  </span></span><span>To carry out price analysis and conduct purchase / contract negotiation\n  with vendors to ensure materials / services are acquired at best pricing.</span></p>\n  <p class=\"MsoListParagraph\" style=\"text-indent: -0.25in\"><span>-<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n  </span></span><span>To hasten suppliers for prompt deliveries in the orders.</span></p>\n  <p class=\"MsoListParagraph\" style=\"text-indent: -0.25in\"><span>-<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n  </span></span><span>To ensure department KPI met.</span></p>\n  <p class=\"MsoListParagraph\" style=\"text-indent: -0.25in\"><span>-<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n  </span></span><span>Generation of Purchase Order and process change order/s.</span></p>\n  <p class=\"MsoListParagraph\" style=\"text-indent: -0.25in\"><span>-<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n  </span></span><span>Liaise with respective Division/s and other functional personnel.</span></p>\n  <p class=\"MsoListParagraph\" style=\"text-indent: -0.25in\"><span>-<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n  </span></span><span>Provide regular feedback on material or services status to requests.</span></p>\n  <p class=\"MsoListParagraph\" style=\"text-indent: -0.25in\"><span>-<span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n  </span></span><span>Any other procurement matters\n  assign from time to time.</span></p>\n  <p class=\"MsoNormal\"><span>&nbsp;</span></p>\n  </td>\n </tr>\n</tbody></table>", "Procurement Engineer", "", "publish", "closed", "closed", "", "procurement-engineer", "", "", "2017-07-06 09:30:30", "2017-07-06 01:30:30", "", "0", "http://localhost/enggsol/jobs/procurement-engineer/", "0", "jobs", "", "0");
INSERT INTO `wp_posts` VALUES("3694", "1", "2017-06-13 14:09:27", "2017-06-13 06:09:27", "<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal;\"><b><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Position:&nbsp; System Engineers</span></b></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal;\"><b><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">&nbsp;</span></b></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal;\"><b><u><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Job Requirement:</span></u></b></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">&nbsp;</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">A.&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Azure\nStack and Windows Server 2016 skills:</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">A.1 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; <b>Azure\nStack skills:</b></span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 0.5in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">MCSA or Higher - preferably, with MCSA for Server\n2016.&nbsp; Minimally, MCSA for Server 2012.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 0.5in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Preferably with Azure Cloud experience/certification.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 0.5in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Preferably with knowledge/experience in automation of\nservices.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">&nbsp;</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">A.2&nbsp; &nbsp;&nbsp;&nbsp;&nbsp; Windows\nServer 2016 skills:</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 0.5in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">MCSA or Higher - preferably, with MCSA for Server\n2016.&nbsp; Minimally, MCSA for Server 2012.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 0.5in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Preferably with at least 3 years experience in Windows\nServer Design, Setup, and Administration.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 0.5in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Preferably with knowledge/experience of Powershell.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 0.5in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Preferably with knowledge/experience of Azure Pack, esp.\nsetting up of templates for resource automation.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 0.5in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Preferably with knowledge/experience of Hyper-V.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 0.5in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Preferably with knowledge/experience of System Center 2016\n(minimally, System Center 2012), including SCOM, SCCM, SCSM.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 0.5in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Preferably with experience in Data Center environment.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">&nbsp;</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">&nbsp;</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">B.&nbsp; &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Network\nand Security Specialist skills:</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">B.1 &nbsp;&nbsp;&nbsp;&nbsp;&nbsp; Network Skills</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 35.45pt;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">CCNP (minimally CCNA) - preferably with experience\nusing/configuring Cisco Nexus 9000 series switches.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 35.45pt;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Preferably familiar with Cisco ACI configuration and\nmanagement.<br />\n<br />\nB.2.&nbsp; &nbsp;&nbsp;&nbsp; Security\nSpecialist skills:</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 35.45pt;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">CCNP (minimally CCNA) - preferably with experience\nusing/configuring Cisco security appliances.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 35.45pt;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Preferably with security related certification - e.g CISSP,\nIT Security Grad. Dip., etc.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;text-indent: 35.45pt;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">&nbsp;</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal;\"><b><u><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">Qualifications:</span></u></b></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">&nbsp;</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal;\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif;\">- Degree in Computer\nEngineering, Computer Science, IT, Electrical &amp; Electronics Engineering or\nrelated disciplines with at least 3 years of direct working experience in the\nareas mentioned above would be an advantage.</span></p>", "System Engineers", "", "publish", "closed", "closed", "", "system-engineers", "", "", "2017-06-13 15:49:33", "2017-06-13 07:49:33", "", "0", "http://localhost/enggsol/jobs/system-engineers/", "0", "jobs", "", "0");
INSERT INTO `wp_posts` VALUES("3695", "1", "2017-06-13 14:09:27", "2017-06-13 06:09:27", "<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal\"><b><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Position:&nbsp; Senior / Software Engineers&nbsp;</span></b></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal\"><b><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">&nbsp;</span></b></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal\"><b><u><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Key Responsibility:</span></u></b></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">&nbsp;</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Independently design\nand develop application software.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Evaluate and identify\nnew technologies for implementation.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Take part in\ndesigning and development of Product/Project scopes and able to manage\nsubsystem development.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Software analysis,\ncode analysis, unit test, peer review of source code and performance turning.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Ensure the quality of\ndeliverables that meet the requirements and standards for User Acceptance\nTesting.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal\"><b><u><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\"><br />\nJob Requirement:</span></u></b></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">&nbsp;</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Ability to work as a\nteam member.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Ability to work\ncreatively and analytically in a problem-solving environment.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Developing solutions\nusing ASP.NET MVC, AJAX, JSON Javascript/JQuery, CSS. </span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Equipped C#.NET with\nat least 3 years working experience. </span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Database and\nMicrosoft SQL Server environment.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Familiar with web\nservices (WCF/WEB API/SingalR) .</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Familiar with Web\nUser Interface Design HTML5, CSS, Bootstrap.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Familiar with common\ndevelopment tools such as Visual Studio, MSSQL Server Management Studio.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">-  Preferably knowledge/experience with ITIL v3\nknowledge and System Center Service Manager (2016) knowledge.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Working on\nmulti-tiered Client/Server or Web based applications.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal\"><b><u><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\"><br />\nQualifications:</span></u></b></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">&nbsp;</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Degree in Computer\nEngineering, Computer Science, Electrical &amp; Electronics Engineering or\nrelated disciplines with at least 3 years of direct working experience in the\nareas mentioned above would be an advantage.</span></p>", "Senior / Software Engineers", "", "publish", "closed", "closed", "", "senior-software-engineers", "", "", "2017-07-06 09:30:30", "2017-07-06 01:30:30", "", "0", "http://localhost/enggsol/jobs/senior-software-engineers/", "0", "jobs", "", "0");
