INSERT INTO `wp_posts` VALUES("3723", "0", "2017-06-21 18:28:02", "2017-06-21 10:28:02", "<div><b><span lang=\"EN\">Responsibilities:</span></b></div>\n\n<ul>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">Manage a\n     team of engineers to ensure services are delivered to customer as per\n     contract.</span></li>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">Working\n     closely with Team Lead and Tech Lead to meet day to day operations</span></li>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">Manage\n     project P&amp;L</span></li>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">Responsible\n     for s<b>ervice delivery and operation readiness; meeting the KPI</b></span></li>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">Ensuring\n     Service Quality Assurance</span></li>\n</ul>\n\n<p class=\"MsoNormal\"><span lang=\"EN\">&nbsp;</span><b><span lang=\"EN\">Requirements:</span></b></p>\n\n<ul>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">3 to 5\n     years of <b>Service Delivery Management </b>in an outsource environment</span></li>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">Good\n     Customer handling and managing a team of <b>delivery engineers.</b></span></li>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">Previous\n     <b>technical hands on experience ( networks or systems) is desirable</b></span></li>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">At least\n     ITIL foundation v3 and one or two Intermediate Intermediate Life-cycle\n     Module certification.</span>&nbsp;</li></ul>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 9.0pt\"><b><span lang=\"EN\">Personal Attributes</span></b><span lang=\"EN\"></span></p>\n\n<ul>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">Highly\n     motivated</span></li>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">Team\n     Player</span></li>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">Excellent\n     written and oral communication skills</span></li>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">Excellent\n     presentation skills</span></li>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">Self-starter</span></li>\n</ul>", "Service Delivery Manager (Perm)", "", "publish", "closed", "closed", "", "service-delivery-manager-perm", "", "", "2017-07-06 09:30:30", "2017-07-06 01:30:30", "", "0", "http://localhost/enggsol/jobs/service-delivery-manager-perm/", "0", "jobs", "", "0");
INSERT INTO `wp_posts` VALUES("3724", "0", "2017-07-04 07:42:59", "2017-07-03 23:42:59", "<p class=\"MsoNormal\"><b><span lang=\"EN\">Responsibilities:</span></b><span lang=\"EN\"></span></p>\n\n<ul>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">To be part\n     of the&nbsp;network operations team to <b>coordinate and assist with project\n     work.</b></span></li>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">Responsible\n     to ensure smooth execution of project changes and see to the completion of\n     the <b>project for handover to Network operations team.</b></span></li>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">Be on <b>24x7\n     standby duties as required for project.</b></span></li>\n <li class=\"MsoNormal\" style=\"margin-left: 0.0in\"><span lang=\"EN\">Perform\n     <b>network administration </b>tasks as assigned.</span></li>\n</ul>\n\n<p class=\"MsoNormal\"><b><span lang=\"EN\">Requirements:</span></b></p><ul><li>Routing and switching knowledge with at least<b> CCNA qualifications.</b></li><li>4 to 5 years of relevant working experience.</li><li>Hands-on configurations with <b>Cisco switches/routers</b> is a must.&nbsp;</li><li>Intermediate Knowledge of <b>F5 LTM/GTM , Cisco ASA and checkpoint Firewalls</b>\n     is preferred.</li><li>Needs to have at least 3 years of <b>relevant Project Management experience</b></li><li>Able\n     to work independently with minimum supervision</li></ul><ol>\n</ol>", "Project Engineer (Network)", "", "publish", "closed", "closed", "", "project-engineer-network", "", "", "2017-07-06 09:30:29", "2017-07-06 01:30:29", "", "0", "http://localhost/enggsol/jobs/project-engineer-network/", "0", "jobs", "", "0");
INSERT INTO `wp_posts` VALUES("3725", "0", "2017-07-04 07:42:59", "2017-07-03 23:42:59", "<p class=\"MsoNormal\"><span lang=\"EN\">The JD is as follows: </span></p>\n\n<ul>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Analyse complex technical\n     requirements and work with Solution Architect on bid strategies,\n     compliance responses and propose video solutions</span></li>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Drive system demo and POC (Proof\n     of Concept) as part of pre-tender engagement and post-tender submission\n     engagement</span></li>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">To support Request for Quotation\n     (RFQ), evaluate third-party technologies/solutions, negotiate and make\n     recommendations to bid team for inclusion in proposal</span></li>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Give support to project team in\n     project implementation upon award, including driving system level and\n     detailed design, Acceptance Test Plans, and seek approvals from customers\n     and regulatory authorities where applicable, and manage 3rd party vendors\n     on delivery and performance.</span></li>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Conduct and develop materials for\n     use in customer training and train others in project teams or life-cycle\n     support services team</span></li>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Conduct project post mortem,\n     identify successful elements and propose recommendations on areas for\n     improvement</span></li>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Follow up on implemented solutions\n     and identifies new opportunities that complement the work that was\n     completed</span></li>\n</ul>\n\n<p class=\"MsoNormal\"><span lang=\"EN\">Ideal candidate should possess:</span></p>\n\n<ul>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Degree in Electrical and\n     Electronics, Telecommunications, Information Technology or equivalent.</span></li>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Minimum 10 years experience in\n     Physical Security and sound IT Network System knowledge. Extensive\n     experience in handling presales function and design of large scale\n     enterprise Video System for tenders.</span></li>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Hands-on professional with strong\n     technical, analytical, and organizational skills associated with the\n     design and implementation of video delivery on IP based networks in\n     Service Provider and/or Public Safety environments</span></li>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Knowledge of both wired and\n     wireless (wi-fi, wi-Max, 4G LTE) video transmission technologies including\n     Adaptive Video Streaming, etc.</span></li>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Experience with various Video\n     Management System like Genetec, Milestone, OnSSI, Pelco, Avigilon and\n     HuaWei VCM software are a plus</span></li>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Experience with various IP Cameras\n     like Avigilon, Axis, Bosch, Samsung, Sony, and Pelco surveillance cameras\n     are a plus</span></li>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Experience with server and storage\n     hardware components including RAID levels and virtualization.</span></li>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Experience with Access Control,\n     Intrusion Detection Systems, Security Systems and /or Wireless Networks\n     are a plus.</span></li>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Familiar with video analytics\n     (both backend and at the edge) solution, including deep learning based\n     analytics.&nbsp;</span></li>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Experience with Microsoft OS,\n     Linux OS and VMWare.</span></li>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Strong leadership coupled with\n     excellent client, vendor and team management skills as well as good\n     interpersonal and negotiation skills&nbsp;&nbsp;</span></li>\n <li class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Excellent written and verbal\n     communications skills with demonstrated ability to clearly articulate\n     complex technical solution to customers and consultants</span></li>\n</ul>", "Presales Consultant -Video Systems", "", "publish", "closed", "closed", "", "presales-consultant-video-systems", "", "", "2017-07-06 09:30:29", "2017-07-06 01:30:29", "", "0", "http://localhost/enggsol/jobs/presales-consultant-video-systems/", "0", "jobs", "", "0");
INSERT INTO `wp_posts` VALUES("3726", "0", "2017-07-04 07:42:59", "2017-07-03 23:42:59", "<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal\"><b><u><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Job Requirement:</span></u></b></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal\"><b><u><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">&nbsp;</span></u></b></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Good understanding of\nPC operating system such as Windows 2000, Windows XP.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Good understanding of\nPC hardware maintenance such as RAM, hardisk, CPU, power supply etc.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Ability to\ncommunicate intelligently and effectively with fellow staff performing\nmaintenance or upgrade work on PC installed at designated work environment.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Ability to install,\nconfigure and commission PC and other related equipment such as printers,\nscanners and copiers.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Experience in troubleshooting\nPC related problems.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">&nbsp;</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">&nbsp;</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal\"><b><u><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Qualifications:</span></u></b></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">&nbsp;</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Diploma in\nappropriate field </span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-family: Arial , sans-serif;font-size: 10.0pt\">&nbsp;</span></p>", "Desktop engineer", "", "publish", "closed", "closed", "", "desktop-engineer", "", "", "2017-07-06 09:30:30", "2017-07-06 01:30:30", "", "0", "http://localhost/enggsol/jobs/desktop-engineer/", "0", "jobs", "", "0");
INSERT INTO `wp_posts` VALUES("3727", "0", "2017-07-04 07:43:00", "2017-07-03 23:43:00", "<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal\"><b><u><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Key Responsibility:</span></u></b></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">&nbsp;</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"text-indent: -7.1pt\">Site supervision</span></p><p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"text-indent: -7.1pt\">Managing contractors</span></p><p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"text-indent: -7.1pt\">Attend site meeting\nand liaison with customers</span></p><p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"text-indent: -7.1pt\">Knowledge of M&amp;E\nsystem for data centre</span></p>\n\n\n\n\n\n\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal\"><b><u><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\"><br />\nJob Requirement:</span></u></b></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">&nbsp;</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Good technical\nknowledge with an eye for details</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">Able to work within\ntight schedules and fast turn-around time</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><br /></p>\n\n<p class=\"MsoNormal\" style=\"margin-bottom: 0.0in;line-height: normal\"><b><u><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\"><br />\nQualifications:</span></u></b></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">&nbsp;</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-top: 0.0in;margin-right: 0.0in;margin-bottom: 0.0in;margin-left: 42.55pt;text-indent: -7.1pt;line-height: normal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">- Degree in Mechanical\nor Building Services Engineering</span></p>", "Project Engineers", "", "publish", "closed", "closed", "", "project-engineers", "", "", "2017-07-06 09:30:30", "2017-07-06 01:30:30", "", "0", "http://localhost/enggsol/jobs/project-engineers/", "0", "jobs", "", "0");
INSERT INTO `wp_posts` VALUES("3728", "0", "2017-07-04 07:43:00", "2017-07-03 23:43:00", "<p class=\"MsoNormal\"><span style=\"font-size: 10.0pt;font-family: Arial , sans-serif\">&nbsp;</span></p>\n\n<p class=\"MsoNormal\"><span lang=\"EN\" style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">T<span>he Agile Scrum Master\nConsultant will be responsible for upholding the values of Scrum so as to\nensure that quality software is delivered by the team according to\nspecification and the correct procedures are followed in line with technical\nand business objectives and company strategy. He or she is also responsible for\nmotivating and leading the people in the team to create a high trusting and\nhigh efficient working environment who embraces a strong learning culture. </span></span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Educate\ncustomers and project team on Agile Processes and values.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Single\npoint of contact to customer teams.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Ensure\nthe Scrum ceremonies are correctly carried out with the appropriate displayed\nvalues.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Plan,\nlead and track progress of software development work.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Map\nout overall execution plan and ensure that roles &amp; responsibility,\nobjectives, commitment, deliverables and schedule are clearly communicated to\nteam members &amp; customers.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Understand\nand analyze requirements to assist in development of technical design\nspecification and translating design into process/program specifications.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Taking\nlead of projects.&nbsp;</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Able\nto work with the customer product owner to prioritize project work and\ntechnical debt to ensure optimum use of technical resources.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Managing\ncustomers expectations.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Ensuring\nquality in the development process and providing ongoing maintenance.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Coach,\ntrain and mentor team members</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Ensure\nteam members receive relevant training to complete their tasks.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Responsible\nfor clear communication (both written and verbal) with technical and\nnon-technical contacts (internal and external) and all seniority levels.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Maintaining\na good relationship with the customer.</span></p>\n\n<p class=\"MsoNormal\"><span lang=\"EN\"><br />\nIdeal candidate should possess the followings:&nbsp; </span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">At\nleast&nbsp;12 years of experience with substantial project management work in\nsoftware design &amp; development, of which at least&nbsp;4 years are in agile\nprojects</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Minimally&nbsp;8\nyears of Scrum-Master experience and have completed projects with strong need\nfor User Experience</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Prior\nexposure to agile development methodologies such as SCRUM and XP Programming or\nKanban.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Prior\nexperience in using user stories to document requirements will be a plus</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Experience\nin developing solutions using appropriate platforms and system technologies\nsuch as enterprise database design, web and application server and network\ntechnologies</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Enjoy\nworking in a team and apply adaptive, servant leadership according to the\ndifferent working scenarios</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Self-motivated\nwith a positive can-do attitude, creative with excellent presentation,\ncommunication, negotiation and interpersonal skills including strong persuasive\ntechniques.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Ability\nto lead, develop and maintain respectful and trusting relationship;</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Proficient\nin written and spoken English</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Agile\ncertifications in CSM, CSP,&nbsp; PSM, PPO or PMI-ACP.</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.25in\"><span lang=\"EN\">Project\nmanagement certifications in PMP, CITPM or Prince2 will be an advantage</span></p>", "Scrum Master", "", "publish", "closed", "closed", "", "scrum-master", "", "", "2017-07-06 09:30:30", "2017-07-06 01:30:30", "", "0", "http://localhost/enggsol/jobs/scrum-master/", "0", "jobs", "", "0");
