INSERT INTO `wp_posts` VALUES("3730", "0", "2017-07-04 07:43:00", "2017-07-03 23:43:00", "<div><b>MAIN DUTIES AND RESPONSIBILITIES:</b></div><div><br /></div><div>Design and develop machine to meet the system specifications (required by process/ automation needs) involving:</div><div>Design standardization</div><div>Costing and project planning</div><div>Machine and module conceptualization design</div><div>Design for manufacturing (DFM), design for assembly (DFA) and Concurrent engineering</div><div>Pneumatic/ hydraulic circuit design</div><div>Detail fabrication drawings, assembly drawings and layout drawings</div><div>Selection and sizing of standard components available off the shelf</div><div><br /></div><div><b>EDUCATION AND EXPERIENCE</b></div><div><br /></div><div>Bachelor/ Master of Engineering with minimum 5 years relevant experience</div><div>3D solid Modeling, preferably Pro-E (others like Solid Edge, Auto Inventor, Mechanical Desktop, Solid Designer are also acceptable).</div><div><b>Good track record in:</b></div><div>Ultra High Precision machine design</div><div>High Speed Pick and Place mechanism design</div><div>Structural, kinematics and dynamic analysis and FEA simulation</div><div>Geometrical tolerance and dimensional analysis (e.g. stack up error)</div><div>Engineering material selection</div><div>Design troubleshooting</div><div>Application of analysis tools like FMEA, DOE</div><div><br /></div>", "Mechanical Engineer", "", "publish", "closed", "closed", "", "mechanical-engineer", "", "", "2017-07-06 09:30:30", "2017-07-06 01:30:30", "", "0", "http://localhost/enggsol/jobs/mechanical-engineer/", "0", "jobs", "", "0");
INSERT INTO `wp_posts` VALUES("3731", "0", "2017-07-04 07:43:00", "2017-07-03 23:43:00", "<p class=\"MsoNormal\"><b><span>Primary Purpose</span></b><span></span></p>\n\n<p class=\"MsoNormal\"><span>&nbsp;</span></p>\n\n<p class=\"MsoNormal\"><span>Responsible for the construction, testing, installation,\noperations &amp; facility management of client platforms to meet agreed\nbusiness needs &amp; service levels.</span></p>\n\n<p class=\"MsoNormal\"><span>&nbsp;</span></p>\n\n<p class=\"MsoNormal\"><span>&nbsp;</span></p>\n\n<p class=\"MsoNormal\"><b><span>Responsibilities</span></b><span></span></p>\n\n<ul>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Perform troubleshooting for client platform related\n     problems.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Respond promptly to incident, investigate &amp; provide\n     temporary &amp;/or permanent resolution of incidents escalated.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Provide timely status updates to relevant parties.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Perform service request related to client platforms.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Monitor the agreed service level, document &amp;\n     maintain the configuration of the systems; provide regular reporting to\n     relevant parties.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Involve in client platform implementation/upgrade\n     project.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Manage client platform implementation/upgrade project.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Perform on research/evaluation of client platform\n     tools.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Provide systems related technical advice to customers\n     or project team.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Manage client platform management systems.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Manage systems changes through change request process\n     &amp; provide status reports to the relevant parties.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Perform client platform related presales tasks.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Gather business &amp;/or application requirements on\n     the infrastructure to perform the infrastructure requirement analysis\n     &amp; design.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Ensure service level meet requirement. Propose and\n     implement action plan when required.</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Established facility management standards/best\n     practices to ensureoperation consistency across project/facility\n     management teams.</span></li>\n</ul>\n\n<p class=\"MsoNormal\"><span>&nbsp;</span></p>\n\n<p class=\"MsoNormal\"><b><span>Ideal candidate should possess the following:</span></b><span></span></p>\n\n<ul>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Diploma in IT / Computer Engineering and above</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Min 3 years of experience in client platform\n     technologies including desktop, mobile and servers</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Windows server skills is a must including setup,\n     configuration, troubleshooting</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Knowledge in Microsoft MOM, SCOM, SCCM is preferred</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Security products such as McAfee AV, DLP, etc. is\n     preferred</span></li>\n <li class=\"MsoNormal\"><span style=\"font-size: 9.0pt;font-family: Arial , sans-serif\">Good team player with good communication skills</span></li>\n</ul>", "SCCM CONSULTANT", "", "publish", "closed", "closed", "", "sccm-consultant", "", "", "2017-07-06 09:30:30", "2017-07-06 01:30:30", "", "0", "http://localhost/enggsol/jobs/sccm-consultant/", "0", "jobs", "", "0");
INSERT INTO `wp_posts` VALUES("3732", "0", "2017-07-06 09:30:29", "2017-07-06 01:30:29", "<p class=\"MsoNormal\" style=\"background: white\"><b><span>Job Description:</span></b><span></span></p><p class=\"MsoNormal\" style=\"background: white\"><b><span><br /></span></b></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.0in;text-indent: -0.25in;background: white\"><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n</span></span><span>Provide general administrative support to executives and\nmanagers in the company</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.0in;text-indent: -0.25in;background: white\"><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n</span></span><span>Provide sales delivery and billing administrative support to\ndepartment</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.0in;text-indent: -0.25in;background: white\"><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n</span></span><span>Maintain and update companys centralized document and record\nfiling systems which includes service report, PR, Capex, Asset records, HR\nmatters, classified documents</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.0in;text-indent: -0.25in;background: white\"><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n</span></span><span>Managing company asset including record, asset labeling and\ntracking, stationary and office equipment</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.0in;text-indent: -0.25in;background: white\"><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n</span></span><span>Assist in other ad-hoc work assignment when required</span></p><p class=\"MsoNormal\" style=\"margin-left: 0.0in;text-indent: -0.25in;background: white\"><span><br /></span></p>\n\n<p class=\"MsoNormal\" style=\"background: white\"><b><span>Job Requirement:</span></b><span></span></p><p class=\"MsoNormal\" style=\"background: white\"><b><span><br /></span></b></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.0in;text-indent: -0.25in;background: white\"><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n</span></span><span>GCE A Levels / Diploma in relevant discipline</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.0in;text-indent: -0.25in;background: white\"><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n</span></span><span>Good communication and interpersonal skills</span></p>\n\n<p class=\"MsoNormal\" style=\"margin-left: 0.0in;text-indent: -0.25in;background: white\"><span><span>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;\n</span></span><span>Proficient in Microsoft Office</span></p>\n\n<p class=\"MsoNormal\"><br /></p>", "Admin Executive", "", "publish", "closed", "closed", "", "admin-executive", "", "", "2017-07-06 09:30:29", "2017-07-06 01:30:29", "", "0", "http://localhost/enggsol/jobs/admin-executive/", "0", "jobs", "", "0");
INSERT INTO `wp_posts` VALUES("3733", "0", "2017-07-06 09:30:29", "2017-07-06 01:30:29", "<ol style=\"margin-top: 0.0in\">\n <li class=\"MsoNormal\">Working experience both\n     delivery and presales for at least 5-7 years</li>\n <li class=\"MsoNormal\">Possess network\n     certification on networking, wireless.</li>\n <li class=\"MsoNormal\">Softskill wise; able to\n     conduct presentation and good writing skill</li>\n <li class=\"MsoNormal\">Good wireless network\n     experience will be an advantage</li>\n</ol>", "Presales Consultant - Wireless Network", "", "publish", "closed", "closed", "", "presales-consultant-wireless-network", "", "", "2017-07-06 09:30:29", "2017-07-06 01:30:29", "", "0", "http://localhost/enggsol/jobs/presales-consultant-wireless-network/", "0", "jobs", "", "0");
